mapper.select:
SELECT COUNT(*) FROM homework h WHERE teacher_id = #{teacherId}
AND homework_id LIKE CONCAT('%', #{homeworkId}, '%')
AND homework_title LIKE CONCAT('%', #{homeworkTitle}, '%')

SELECT homework_id AS 'homeworkId', t.teacher_id AS 'teacherId', teacher_name AS 'teacherName', homework_title AS 'homeworkTitle', homework_content AS 'homeworkContent' FROM homework JOIN teacher t on homework.teacher_id = t.teacher_id WHERE t.teacher_id = #{teacherId}
AND homework_id LIKE CONCAT('%', #{homeworkId}, '%')
AND homework_title LIKE CONCAT('%', #{homeworkTitle}, '%')
